Scripts.OnMouseDown:
if ( self.down ) then
    self.down = nil;
    if ReckoningAchievementFrame:IsShown() then
        ReckoningAchievementFrame:Hide();
    else
        ReckoningAchievementFrame:Show();
    end
    ReckoningAchievementMicroButton_UpdateIcon();
    return;
end
ReckoningAchievementMicroButton_SetPushed();
self.down = 1;

Scripts.OnMouseUp:
if ( self.down ) then
    self.down = nil;
    if ( self:IsMouseOver() ) then
        if ReckoningAchievementFrame:IsShown() then
            ReckoningAchievementFrame:Hide();
        else
            ReckoningAchievementFrame:Show();
        end
    end
    ReckoningAchievementMicroButton_UpdateIcon();
    return;
end
if ( self:GetButtonState() == "NORMAL" ) then
    ReckoningAchievementMicroButton_SetPushed();
    self.down = 1;
else
    ReckoningAchievementMicroButton_SetNormal();
    self.down = 1;
end
